# –†–∞—Å—á—ë—Ç —Å—É–º–º—ã —Ü–∏—Ñ—Ä –¥–æ –º–∞—Å—Ç–µ—Ä-—á–∏—Å–µ–ª –∏–ª–∏ –æ–¥–Ω–æ–∑–Ω–∞—á–Ω—ã—Ö

def reduce_number(n):
    while n not in (11, 22, 33) and n > 9:
        n = sum(int(d) for d in str(n))
    return n

def calculate_numbers(birthdate):
    digits = [int(d) for d in birthdate.strftime("%d%m%Y")]
    total = sum(digits)
    destiny = reduce_number(total)

    day = reduce_number(birthdate.day)
    month = reduce_number(birthdate.month)
    year = reduce_number(reduce_number(birthdate.year))

    soul = reduce_number(day + month)
    karma = reduce_number(month + year)
    gift = reduce_number(day + year)

    return {
        "–ü—Ä–µ–¥–Ω–∞–∑–Ω–∞—á–µ–Ω–∏–µ": destiny,
        "–î–∞—Ä": gift,
        "–ö–∞—Ä–º–∞": karma,
        "–î—É—à–∞": soul
    }

# UI
birthdate = st.date_input("–í–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É —Ä–æ–∂–¥–µ–Ω–∏—è", value=datetime(1984, 10, 23))
if st.button("–†–∞—Å—Å—á–∏—Ç–∞—Ç—å –º–∞—Ç—Ä–∏—Ü—É"):
    results = calculate_numbers(birthdate)
    for key, value in results.items():
        data = ARCHETYPES.get(value, {})
        st.subheader(f"üîπ {key}: {value} ‚Äî {data.get('–∞—Ä—Ö–µ—Ç–∏–ø', '–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö')}")
        st.markdown(f"üü¢ **–°–≤–µ—Ç:** {data.get('—Å–≤–µ—Ç', '-')}")
        st.markdown(f"üî¥ **–¢–µ–Ω—å:** {data.get('—Ç–µ–Ω—å', '-')}")
        st.markdown(f"üí° **–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏:** {data.get('—Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏', '-')}")
